ackground-color: var(--infoScrollThumb);\n}\nmaterial-dialog ::ng-deep main {\n  scrollbar-color: var(--infoScrollThumb) var(--infoBodyBack);\n}\nmaterial-dialog ::ng-deep .wrapper > header, material-dialog[headered] ::ng-deep .wrapper > header {\n  background-color: var(--infoHeaderBack);\n}\nmaterial-dialog ::ng-deep .wrapper > header {\n  /*! @noflip */\n  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);\n}\nmaterial-dialog ::ng-deep .wrapper > main, material-dialog[headered] ::ng-deep .wrapper > main {\n  padding: 0;\n}\n\nmaterial-dialog.headered-dialog ::ng-deep .wrapper > footer, material-dialog.headered-dialog[headered] ::ng-deep .wrapper > footer {\n  background-color: var(--infoHeaderBack);\n  color: var(--infoHeaderFore);\n  padding: 0;\n}\nmaterial-dialog.headered-dialog[headered] ::ng-deep .wrapper ::ng-deep h1 {\n  background-color: var(--infoHeaderBack);\n  color: var(--infoHeaderFore);\n}\n\ndiv[header] {\n  display: flex;\n  flex-direction: row;\n  justify-content: space-between;\n  color: var(--infoHeaderFore);\n  align-items: center;\n}\ndiv[header] material-icon#type {\n  padding-right: 0.5em;\n}\ndiv[header] material-icon.warning {\n  color: var(--infoWarningColor);\n}\ndiv[header] material-icon.error {\n  color: var(--infoErrorColor);\n}\ndiv[header] material-icon.info {\n  color: var(--infoInfoColor);\n}\n\ndiv[footer] {\n  color: var(--infoHeaderFore);\n}\n\n.root {\n  flex: 1;\n  padding: 1em;\n  font-size: 1.2em;\n  color: var(--infoBodyFore);\n  background-color: var(--infoBodyBack);\n  text-align: justify;\n}\n\nh1 {\n  margin-top: 0em;\n  margin-bottom: 0.5em;\n  display: flex;\n  align-items: center;\n}\n\na, a:visited, a:active {\n  text-decoration: none;\n  cursor: pointer;\n  color: var(--infoHeaderBack);\n}\n\nmaterial-button {\n  text-transform: none;\n  font-weight: bold;\n}\n\n.btn {\n  font-weight: bold;\n  cursor: pointer;\n}\n'];